BEGIN TRANSACTION;
CREATE TABLE appointments (
	appointment_id INTEGER NOT NULL, 
	horse_id INTEGER, 
	owner_id INTEGER, 
	vet_id INTEGER, 
	location_id INTEGER, 
	appointment_datetime DATETIME DEFAULT (CURRENT_TIMESTAMP) NOT NULL, 
	duration_minutes INTEGER, 
	reason VARCHAR(255), 
	notes TEXT, 
	status VARCHAR(50), 
	is_confirmed BOOLEAN, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (appointment_id), 
	FOREIGN KEY(horse_id) REFERENCES horses (horse_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id), 
	FOREIGN KEY(vet_id) REFERENCES veterinarians (vet_id), 
	FOREIGN KEY(location_id) REFERENCES locations (location_id)
);
CREATE TABLE charge_code_categories (
	category_id INTEGER NOT NULL, 
	name VARCHAR(100) NOT NULL, 
	parent_id INTEGER, 
	level INTEGER NOT NULL, 
	is_active BOOLEAN NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (category_id), 
	FOREIGN KEY(parent_id) REFERENCES charge_code_categories (category_id)
);
CREATE TABLE charge_codes (
	id INTEGER NOT NULL, 
	code VARCHAR(20) NOT NULL, 
	alternate_code VARCHAR(50), 
	description VARCHAR(255) NOT NULL, 
	category_id INTEGER, 
	standard_charge NUMERIC(10, 2) NOT NULL, 
	is_active BOOLEAN NOT NULL, 
	taxable BOOLEAN, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(category_id) REFERENCES charge_code_categories (category_id)
);
CREATE TABLE communication_logs (
	log_id INTEGER NOT NULL, 
	summary VARCHAR(255), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (log_id)
);
CREATE TABLE company_profile (
	id INTEGER NOT NULL, 
	company_name VARCHAR(255) NOT NULL, 
	address_line1 VARCHAR(255), 
	address_line2 VARCHAR(255), 
	city VARCHAR(100), 
	state VARCHAR(100), 
	zip_code VARCHAR(20), 
	phone VARCHAR(50), 
	email VARCHAR(255), 
	website VARCHAR(255), 
	logo_path VARCHAR(512), 
	notes TEXT, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (id)
);
CREATE TABLE documents (
	document_id INTEGER NOT NULL, 
	file_path VARCHAR(255), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (document_id)
);
CREATE TABLE drugs (
	drug_id INTEGER NOT NULL, 
	name VARCHAR(100), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (drug_id)
);
CREATE TABLE horse_locations (
	id INTEGER NOT NULL, 
	horse_id INTEGER NOT NULL, 
	location_id INTEGER NOT NULL, 
	date_arrived DATE NOT NULL, 
	date_departed DATE, 
	notes TEXT, 
	is_current_location BOOLEAN NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(horse_id) REFERENCES horses (horse_id), 
	FOREIGN KEY(location_id) REFERENCES locations (location_id)
);
CREATE TABLE horse_owners (
	horse_id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	percentage_ownership NUMERIC(5, 2), 
	PRIMARY KEY (horse_id, owner_id), 
	FOREIGN KEY(horse_id) REFERENCES horses (horse_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id)
);
CREATE TABLE horses (
	horse_id INTEGER NOT NULL, 
	horse_name VARCHAR(255) NOT NULL, 
	account_number VARCHAR(50), 
	breed VARCHAR(100), 
	color VARCHAR(50), 
	sex VARCHAR(20), 
	date_of_birth DATE, 
	height_hands NUMERIC(4, 2), 
	chip_number VARCHAR(50), 
	tattoo_number VARCHAR(50), 
	reg_number VARCHAR(50), 
	brand VARCHAR(50), 
	band_tag VARCHAR(50), 
	description TEXT, 
	is_active BOOLEAN NOT NULL, 
	date_deceased DATE, 
	coggins_date DATE, 
	current_location_id INTEGER, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (horse_id), 
	UNIQUE (chip_number), 
	UNIQUE (tattoo_number), 
	FOREIGN KEY(current_location_id) REFERENCES locations (location_id)
);
CREATE TABLE invoices (
	invoice_id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	invoice_date DATE NOT NULL, 
	due_date DATE, 
	subtotal NUMERIC(10, 2) NOT NULL, 
	tax_total NUMERIC(10, 2), 
	grand_total NUMERIC(10, 2) NOT NULL, 
	amount_paid NUMERIC(10, 2) NOT NULL, 
	balance_due NUMERIC(10, 2) NOT NULL, 
	status VARCHAR(50) NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (invoice_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id)
);
CREATE TABLE locations (
	location_id INTEGER NOT NULL, 
	location_name VARCHAR(100) NOT NULL, 
	address_line1 VARCHAR(100), 
	address_line2 VARCHAR(100), 
	city VARCHAR(50), 
	state_code VARCHAR(10), 
	zip_code VARCHAR(20), 
	country_code VARCHAR(10), 
	phone VARCHAR(20), 
	contact_person VARCHAR(100), 
	email VARCHAR(100), 
	is_active BOOLEAN NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (location_id), 
	FOREIGN KEY(state_code) REFERENCES state_provinces (state_code)
);
CREATE TABLE owner_billing_history (
	history_id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	entry_date DATETIME, 
	description VARCHAR(255) NOT NULL, 
	amount_change NUMERIC(10, 2) NOT NULL, 
	new_balance NUMERIC(10, 2) NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (history_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id)
);
CREATE TABLE owner_payments (
	payment_id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	payment_date DATE NOT NULL, 
	amount NUMERIC(10, 2) NOT NULL, 
	payment_method VARCHAR(50) NOT NULL, 
	reference_number VARCHAR(100), 
	notes TEXT, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (payment_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id)
);
CREATE TABLE owners (
	owner_id INTEGER NOT NULL, 
	account_number VARCHAR(20), 
	farm_name VARCHAR(100), 
	first_name VARCHAR(50), 
	last_name VARCHAR(50), 
	address_line1 VARCHAR(100), 
	address_line2 VARCHAR(100), 
	city VARCHAR(50), 
	state_code VARCHAR(10), 
	zip_code VARCHAR(20), 
	phone VARCHAR(20), 
	mobile_phone VARCHAR(20), 
	email VARCHAR(100), 
	is_active BOOLEAN NOT NULL, 
	balance NUMERIC(10, 2), 
	credit_limit NUMERIC(10, 2), 
	billing_terms VARCHAR(50), 
	service_charge_rate NUMERIC(5, 2), 
	discount_rate NUMERIC(5, 2), 
	notes TEXT, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (owner_id), 
	FOREIGN KEY(state_code) REFERENCES state_provinces (state_code)
);
CREATE TABLE procedures (
	procedure_id INTEGER NOT NULL, 
	name VARCHAR(100), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (procedure_id)
);
CREATE TABLE reminders (
	reminder_id INTEGER NOT NULL, 
	due_date DATE, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (reminder_id)
);
CREATE TABLE roles (
	role_id INTEGER NOT NULL, 
	name VARCHAR(50) NOT NULL, 
	description VARCHAR(255), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (role_id)
);
CREATE TABLE state_provinces (
	state_province_id INTEGER NOT NULL, 
	state_code VARCHAR(10) NOT NULL, 
	state_name VARCHAR(50) NOT NULL, 
	country_code VARCHAR(10) NOT NULL, 
	is_active BOOLEAN NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (state_province_id)
);
CREATE TABLE transactions (
	transaction_id INTEGER NOT NULL, 
	horse_id INTEGER NOT NULL, 
	owner_id INTEGER NOT NULL, 
	invoice_id INTEGER, 
	charge_code_id INTEGER NOT NULL, 
	administered_by_user_id VARCHAR(20), 
	transaction_date DATE NOT NULL, 
	description VARCHAR(255) NOT NULL, 
	quantity NUMERIC(10, 2) NOT NULL, 
	unit_price NUMERIC(10, 2) NOT NULL, 
	total_price NUMERIC(10, 2) NOT NULL, 
	taxable BOOLEAN NOT NULL, 
	item_notes TEXT, 
	status VARCHAR(50) NOT NULL, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (transaction_id), 
	FOREIGN KEY(horse_id) REFERENCES horses (horse_id), 
	FOREIGN KEY(owner_id) REFERENCES owners (owner_id), 
	FOREIGN KEY(invoice_id) REFERENCES invoices (invoice_id), 
	FOREIGN KEY(charge_code_id) REFERENCES charge_codes (id), 
	FOREIGN KEY(administered_by_user_id) REFERENCES users (user_id)
);
CREATE TABLE treatment_logs (
	log_id INTEGER NOT NULL, 
	details VARCHAR(255), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (log_id)
);
CREATE TABLE user_roles (
	user_id VARCHAR(20) NOT NULL, 
	role_id INTEGER NOT NULL, 
	PRIMARY KEY (user_id, role_id), 
	FOREIGN KEY(user_id) REFERENCES users (user_id), 
	FOREIGN KEY(role_id) REFERENCES roles (role_id)
);
CREATE TABLE users (
	user_id VARCHAR(20) NOT NULL, 
	password_hash VARCHAR(255) NOT NULL, 
	user_name VARCHAR(100), 
	email VARCHAR(100), 
	is_active BOOLEAN NOT NULL, 
	last_login DATETIME, 
	printer_id VARCHAR(20), 
	default_screen_colors VARCHAR(100), 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (user_id)
);
CREATE TABLE veterinarians (
	vet_id INTEGER NOT NULL, 
	first_name VARCHAR(50) NOT NULL, 
	last_name VARCHAR(50) NOT NULL, 
	license_number VARCHAR(50), 
	specialty VARCHAR(100), 
	phone VARCHAR(20), 
	email VARCHAR(100), 
	is_active BOOLEAN, 
	created_date DATETIME NOT NULL, 
	modified_date DATETIME NOT NULL, 
	created_by VARCHAR(50), 
	modified_by VARCHAR(50), 
	PRIMARY KEY (vet_id), 
	UNIQUE (license_number), 
	UNIQUE (email)
);
CREATE INDEX ix_roles_role_id ON roles (role_id);
CREATE UNIQUE INDEX ix_roles_name ON roles (name);
CREATE UNIQUE INDEX ix_users_email ON users (email);
CREATE INDEX ix_users_is_active ON users (is_active);
CREATE INDEX ix_users_user_id ON users (user_id);
CREATE INDEX ix_state_provinces_state_province_id ON state_provinces (state_province_id);
CREATE UNIQUE INDEX ix_state_provinces_state_code ON state_provinces (state_code);
CREATE INDEX ix_charge_code_categories_category_id ON charge_code_categories (category_id);
CREATE INDEX ix_charge_code_categories_name ON charge_code_categories (name);
CREATE INDEX ix_charge_code_categories_is_active ON charge_code_categories (is_active);
CREATE INDEX ix_charge_code_categories_parent_id ON charge_code_categories (parent_id);
CREATE INDEX ix_charge_code_categories_level ON charge_code_categories (level);
CREATE INDEX ix_veterinarians_last_name ON veterinarians (last_name);
CREATE INDEX ix_veterinarians_vet_id ON veterinarians (vet_id);
CREATE INDEX ix_owners_last_name ON owners (last_name);
CREATE INDEX ix_owners_email ON owners (email);
CREATE UNIQUE INDEX ix_owners_account_number ON owners (account_number);
CREATE INDEX ix_owners_owner_id ON owners (owner_id);
CREATE INDEX ix_owners_state_code ON owners (state_code);
CREATE INDEX ix_owners_farm_name ON owners (farm_name);
CREATE INDEX ix_charge_codes_id ON charge_codes (id);
CREATE UNIQUE INDEX ix_charge_codes_code ON charge_codes (code);
CREATE INDEX ix_charge_codes_alternate_code ON charge_codes (alternate_code);
CREATE INDEX ix_charge_codes_category_id ON charge_codes (category_id);
CREATE INDEX ix_locations_location_id ON locations (location_id);
CREATE UNIQUE INDEX ix_locations_location_name ON locations (location_name);
CREATE INDEX ix_locations_email ON locations (email);
CREATE INDEX ix_locations_state_code ON locations (state_code);
CREATE INDEX ix_horses_horse_name ON horses (horse_name);
CREATE INDEX ix_horses_account_number ON horses (account_number);
CREATE INDEX ix_horses_horse_id ON horses (horse_id);
CREATE INDEX ix_owner_billing_history_history_id ON owner_billing_history (history_id);
CREATE INDEX ix_owner_billing_history_owner_id ON owner_billing_history (owner_id);
CREATE INDEX ix_owner_payments_payment_id ON owner_payments (payment_id);
CREATE INDEX ix_owner_payments_owner_id ON owner_payments (owner_id);
CREATE INDEX ix_invoices_invoice_id ON invoices (invoice_id);
CREATE INDEX ix_invoices_status ON invoices (status);
CREATE INDEX ix_invoices_owner_id ON invoices (owner_id);
CREATE INDEX ix_horse_locations_is_current_location ON horse_locations (is_current_location);
CREATE INDEX ix_horse_locations_id ON horse_locations (id);
CREATE INDEX ix_appointments_appointment_id ON appointments (appointment_id);
CREATE INDEX ix_transactions_transaction_id ON transactions (transaction_id);
CREATE INDEX ix_transactions_charge_code_id ON transactions (charge_code_id);
CREATE INDEX ix_transactions_owner_id ON transactions (owner_id);
